// Tabela.tsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Tabela.css";
import Formulario from "./Formulario";
import PaginaRelatorio from "./PaginaRelatorio"; // Import PaginaRelatorio
import { format } from "date-fns";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faClose,
  faFile,
  faTableColumns,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";

interface TabelaProps {
  dados: any[];
  atualizarDados: () => void;
}

const Tabela: React.FC<TabelaProps> = ({ dados, atualizarDados }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [showRelatorio, setShowRelatorio] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const formatData = (dataString: string) => {
    return format(new Date(dataString), "dd/MM/yyyy");
  };

  const formatValor = (valorString: string) => {
    const valor = parseFloat(valorString.replace(",", "."));
    return valor.toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });
  };

  const handleDelete = async (id: number) => {
    await axios.delete(`http://localhost:3001/Releases/${id}`);
    atualizarDados();
  };

  const handleRelatorio = () => {
    setShowRelatorio(true);
  };

  useEffect(() => {
    // Coloque a l贸gica de busca de dados aqui se desejar atualizar automaticamente
  }, []);

  return (
    <div className="table-container">
      <div>
        <button onClick={openModal} className="cadastro-button">
          <FontAwesomeIcon
            icon={faTableColumns}
            style={{ marginRight: "5px" }}
          />
          Cadastrar
        </button>
        <button onClick={handleRelatorio} className="relatorio-button">
          <FontAwesomeIcon icon={faFile} style={{ marginRight: "5px" }} />
          Relat贸rio
        </button>
      </div>
      <table className="table">{/* ... (existing table structure) */}</table>

      {modalOpen && (
        <div className="modal">
          <button onClick={closeModal} className="modal-botao">
            <FontAwesomeIcon icon={faClose} style={{ marginRight: "10px" }} />
          </button>
          <Formulario atualizarDados={atualizarDados} />
        </div>
      )}

      {/* Conditional rendering for the Relat贸rio component */}
      {showRelatorio && <PaginaRelatorio />}
    </div>
  );
};

export default Tabela;
