// PaginaRelatorio.tsx
import React, { useEffect, useState } from "react";
import axios from "axios";

interface Release {
  ID: number;
  TIPO: string;
  DESCRICAO: string;
  DATAVENCTO: string;
  VALOR: string;
  DATACRACAO: string;
}

const PaginaRelatorio: React.FC = () => {
  const [releases, setReleases] = useState<Release[]>([]);
  const [aReceberTotal, setAReceberTotal] = useState(0);
  const [aPagarTotal, setAPagarTotal] = useState(0);
  const [saldo, setSaldo] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get("http://localhost:3001/Releases");
        setReleases(result.data);
      } catch (error) {
        console.error("Erro ao buscar dados:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    // Ao atualizar as releases, recalcula os totais
    calcularTotais();
  }, [releases]);

  const calcularTotais = () => {
    let aReceber = 0;
    let aPagar = 0;

    releases.forEach((release) => {
      const valor = parseFloat(release.VALOR.replace(",", "."));

      if (release.TIPO === "Receber") {
        aReceber += valor;
      } else if (release.TIPO === "Pagar") {
        aPagar += valor;
      }
    });

    setAReceberTotal(aReceber);
    setAPagarTotal(aPagar);
    setSaldo(aReceber - aPagar);
  };

  return (
    <div>
      <h1>Relatório</h1>
      <div>
        <h2>
          A Receber:{" "}
          {aReceberTotal.toLocaleString("pt-BR", {
            style: "currency",
            currency: "BRL",
          })}
        </h2>
        <h2>
          A Pagar:{" "}
          {aPagarTotal.toLocaleString("pt-BR", {
            style: "currency",
            currency: "BRL",
          })}
        </h2>
        <h2>
          Saldo:{" "}
          {saldo.toLocaleString("pt-BR", {
            style: "currency",
            currency: "BRL",
          })}
        </h2>
      </div>
      {/* Aqui você pode exibir a lista de releases se precisar */}
      <ul>
        {releases.map((release) => (
          <li key={release.ID}>
            {release.TIPO} - {release.DESCRICAO} - {release.VALOR}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PaginaRelatorio;
