// Formulario.tsx
import React, { useState } from "react";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave } from "@fortawesome/free-solid-svg-icons";
import "./Formulario.css";

interface FormularioProps {
  atualizarDados: () => void;
}

const Formulario: React.FC<FormularioProps> = ({ atualizarDados }) => {
  const [descricao, setDescricao] = useState("");
  const [tipo, setTipo] = useState("");
  const [dataVencimento, setDataVencimento] = useState("");
  const [valor, setValor] = useState("");

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    const data = {
      TIPO: tipo,
      DESCRICAO: descricao,
      DATAVENCTO: dataVencimento,
      VALOR: valor,
      DATACRACAO: new Date().toISOString(),
    };

    await axios.post("http://localhost:3001/Releases", data);

    setDescricao("");
    setTipo("");
    setDataVencimento("");
    setValor("");

    atualizarDados(); // Atualiza os dados após a inserção
  };

  const handleValorChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const valorInput = e.target.value;
    if (/^[0-9]*$/.test(valorInput)) {
      setValor(valorInput);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="formulario">
      {/* Restante do código do formulário */}
    </form>
  );
};

export default Formulario;
