// Tabela.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Tabela.css";
import RelatorioConfronto from "./RelatorioConfronto";
import { format } from "date-fns";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faClose,
  faTableColumns,
  faTrash,
  faFileAlt,
} from "@fortawesome/free-solid-svg-icons";

interface TabelaProps {
  dados: any[];
  atualizarDados: () => void;
}

const Tabela: React.FC<TabelaProps> = ({ dados, atualizarDados }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [dadosAPagar, setDadosAPagar] = useState([]);
  const [dadosAReceber, setDadosAReceber] = useState([]);
  const [resumo, setResumo] = useState({
    totalAPagar: 0,
    totalAReceber: 0,
    saldoLiquido: 0,
  });

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const formatData = (dataString: string) => {
    return format(new Date(dataString), "dd/MM/yyyy");
  };

  const formatValor = (valorString: string) => {
    const valor = parseFloat(valorString.replace(",", "."));
    return valor.toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });
  };

  const handleDelete = async (id: number) => {
    await axios.delete(`http://localhost:3001/Releases/${id}`);
    atualizarDados();
  };

  const handleOpenRelatorio = () => {
    // Calcular dados a pagar, a receber e resumo conforme necessário
    // e, em seguida, abrir o relatório em uma nova aba.
    // (Certifique-se de ajustar esta lógica conforme a sua estrutura de dados e requisitos.)

    // Exemplo de dados fictícios
    const dadosAPagarExemplo = dados.filter((item) => item.TIPO === "Pagar");
    const dadosAReceberExemplo = dados.filter(
      (item) => item.TIPO === "Receber"
    );
    const resumoExemplo = {
      totalAPagar: dadosAPagarExemplo.reduce(
        (total, item) => total + parseFloat(item.VALOR),
        0
      ),
      totalAReceber: dadosAReceberExemplo.reduce(
        (total, item) => total + parseFloat(item.VALOR),
        0
      ),
      saldoLiquido: 0, // Lógica para calcular o saldo líquido
    };

    // Atualizar o estado e abrir o relatório
    setDadosAPagar(dadosAPagarExemplo);
    setDadosAReceber(dadosAReceberExemplo);
    setResumo(resumoExemplo);
    openModal();
  };

  useEffect(() => {
    // Coloque a lógica de busca de dados aqui se desejar atualizar automaticamente
    // atualizarDados();
  }, []);

  return (
    <div className="table-container">
      <div>
        <button onClick={openModal} className="cadastro-button">
          <FontAwesomeIcon
            icon={faTableColumns}
            style={{ marginRight: "5px" }}
          />
          Cadastrar
        </button>
        <button onClick={handleOpenRelatorio} className="relatorio-button">
          <FontAwesomeIcon icon={faFileAlt} style={{ marginRight: "5px" }} />
          Gerar Relatório
        </button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>TIPO</th>
            <th>DESCRIÇÃO</th>
            <th>DATA DE VENCIMENTO</th>
            <th>VALOR</th>
            <th>DATA DE CRIAÇÃO</th>
            <th>Ação</th>
          </tr>
        </thead>
        <tbody>
          {dados.map((item: any) => (
            <tr key={item.ID}>
              <td>{item.ID}</td>
              <td>{item.TIPO}</td>
              <td>{item.DESCRICAO}</td>
              <td>{formatData(item.DATAVENCTO)}</td>
              <td>{formatValor(item.VALOR)}</td>
              <td>{formatData(item.DATACRACAO)}</td>
              <td>
                <button
                  onClick={() => handleDelete(item.ID)}
                  className="delete-button"
                >
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <button onClick={closeModal} className="modal-botao">
            <FontAwesomeIcon icon={faClose} style={{ marginRight: "10px" }} />
          </button>
          <RelatorioConfronto
            dadosAPagar={dadosAPagar}
            dadosAReceber={dadosAReceber}
            resumo={resumo}
          />
        </div>
      )}
    </div>
  );
};

export default Tabela;
