import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Tabela.css";
import Formulario from "./Formulario";
import { format } from "date-fns";

const Tabela: React.FC = () => {
  const [data, setData] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get("http://localhost:3001/Releases");
      setData(result.data);
    };

    fetchData();
  }, []);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  // Função para formatar a data
  const formatData = (dataString: string) => {
    return format(new Date(dataString), "dd/MM/yyyy");
  };

  // Função para formatar o valor
  const formatValor = (valorString: string) => {
    const valor = parseFloat(valorString.replace(",", "."));
    return valor.toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });
  };

  return (
    <div className="table-container">
      <div className="cadastro-button">
        <button onClick={openModal}>Cadastrar</button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Tipo</th>
            <th>Descrição</th>
            <th>Data de Vencimento</th>
            <th>Valor</th>
            <th>Data de Criação</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item: any) => (
            <tr key={item.ID}>
              <td>{item.ID}</td>
              <td>{item.TIPO}</td>
              <td>{item.DESCRICAO}</td>
              <td>{item.DATAVENCTO}</td>
              <td>{item.VALOR}</td>
              <td>{item.DATACRACAO}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <button onClick={closeModal}>Fechar Modal</button>
          <Formulario />
        </div>
      )}
    </div>
  );
};

export default Tabela;
