import React, { useState } from "react";
import axios from "axios";
import "./Formulario.css"; // Certifique-se de ajustar o caminho do arquivo conforme necessário

const Formulario: React.FC = () => {
  const [descricao, setDescricao] = useState("");
  const [tipo, setTipo] = useState("");
  const [dataVencimento, setDataVencimento] = useState("");
  const [valor, setValor] = useState("");

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    // Construa o objeto de dados para enviar ao servidor
    const data = {
      TIPO: tipo,
      DESCRICAO: descricao,
      DATAVENCTO: dataVencimento,
      VALOR: valor,
      DATACRACAO: new Date().toISOString(),
    };

    // Faça uma requisição para a API de inserção
    await axios.post("http://localhost:3001/Releases", data);

    // Limpe os campos após a inserção
    setDescricao("");
    setTipo("");
    setDataVencimento("");
    setValor("");
  };

  return (
    <form onSubmit={handleSubmit} className="formulario">
      <div className="campo">
        <label>
          Tipo:
          <input
            type="text"
            value={tipo}
            onChange={(e) => setTipo(e.target.value)}
          />
        </label>
      </div>
      <div className="campo">
        <label>
          Descrição:
          <input
            type="text"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
          />
        </label>
      </div>
      <div className="campo">
        <label>
          Data de Vencimento:
          <input
            type="date"
            value={dataVencimento}
            onChange={(e) => setDataVencimento(e.target.value)}
          />
        </label>
      </div>
      <div className="campo">
        <label>
          Valor:
          <input
            type="text"
            value={valor}
            onChange={(e) => setValor(e.target.value)}
          />
        </label>
      </div>
      <div className="divBotao">
        <button type="submit" className="botao-inserir">
          Inserir
        </button>
      </div>
    </form>
  );
};

export default Formulario;
