import React, { useEffect, useState } from "react";
import axios from "axios";
import Formulario from "./Formulario";

const Tabela: React.FC = () => {
  const [data, setData] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get("http://localhost:3001/Releases");
      setData(result.data);
    };

    fetchData();
  }, []);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div>
      <button className="cadastro-button" onClick={openModal}>
        Cadastro
      </button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tipo</th>
            <th>Descrição</th>
            <th>Data de Vencimento</th>
            <th>Valor</th>
            <th>Data de Criação</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item: any) => (
            <tr key={item.ID}>
              <td>{item.ID}</td>
              <td>{item.TIPO}</td>
              <td>{item.DESCRICAO}</td>
              <td>{item.DATAVENCTO}</td>
              <td>{item.VALOR}</td>
              <td>{item.DATACRACAO}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <button onClick={closeModal}>Fechar Modal</button>
          <Formulario /> {/* Renderiza o componente Formulario no modal */}
        </div>
      )}
    </div>
  );
};

export default Tabela;
