// PaginaRelatorio.tsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./PaginaRelatorio.css"; // Importe o arquivo CSS

interface Release {
  ID: number;
  TIPO: string;
  DESCRICAO: string;
  DATAVENCTO: string;
  VALOR: string;
  DATACRACAO: string;
}

const PaginaRelatorio: React.FC = () => {
  // ... (código anterior)

  const renderTabela = (data: Release[], tipo: string) => {
    return (
      <div className="table-container">
        <h2>{tipo}</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Descrição</th>
              <th>Valor</th>
              <th>Data de Vencimento</th>
            </tr>
          </thead>
          <tbody>
            {data.map((release) => (
              <tr key={release.ID}>
                <td>{release.DESCRICAO}</td>
                <td>{release.VALOR}</td>
                <td>{release.DATAVENCTO}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan={2}>Subtotal</td>
              <td>{calcularSubtotal(data)}</td>
            </tr>
          </tfoot>
        </table>
      </div>
    );
  };

  const renderButtons = () => (
    <div className="buttons-container">
      <button className="print-button" onClick={() => window.print()}>
        Imprimir
      </button>
      <button className="close-button" onClick={() => window.close()}>
        Fechar
      </button>
    </div>
  );

  // ... (código posterior)

  return (
    <div>
      {renderButtons()}
      <h1>Relatório</h1>
      {renderTabela(aReceber, "A Receber")}
      {renderTabela(aPagar, "A Pagar")}
      <div>
        <h2>Total Geral</h2>
        <p>
          {saldo.toLocaleString("pt-BR", {
            style: "currency",
            currency: "BRL",
          })}
        </p>
      </div>
    </div>
  );
};

export default PaginaRelatorio;
