// src/components/Formulario.tsx
import React, { useState } from "react";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import "./Formulario.css"; // Certifique-se de ajustar o caminho do arquivo conforme necessário

const Formulario: React.FC = () => {
  const [descricao, setDescricao] = useState("");
  const [tipo, setTipo] = useState("");
  const [dataVencimento, setDataVencimento] = useState("");
  const [valor, setValor] = useState<number | string>("");

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    // Construa o objeto de dados para enviar ao servidor
    const data = {
      TIPO: tipo,
      DESCRICAO: descricao,
      DATAVENCTO: dataVencimento,
      VALOR: valor,
      DATACRACAO: new Date().toISOString(),
    };

    // Faça uma requisição para a API de inserção
    await axios.post("http://localhost:3001/Releases", data);

    // Limpe os campos após a inserção
    setDescricao("");
    setTipo("");
    setDataVencimento("");
    setValor("");
  };

  const handleValorChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    // Obtém o valor da entrada
    const valorInput = e.target.value;

    // Remove caracteres não numéricos
    const valorNumerico = parseFloat(valorInput.replace(/[^0-9.,]/g, ""));

    // Formata o valor como moeda (ex: 10,50)
    const formattedValue = valorNumerico.toLocaleString("pt-BR", {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });

    // Atualiza o estado do valor como string
    setValor(formattedValue);
  };

  return (
    <form onSubmit={handleSubmit} className="formulario">
      <div className="campo">
        <label>
          Tipo:
          <select
            value={tipo}
            onChange={(e) => setTipo(e.target.value)}
            className="select-style"
          >
            <option value="">Selecione...</option>
            <option value="Pagar">Pagar</option>
            <option value="Receber">Receber</option>
          </select>
        </label>
      </div>
      <div className="campo">
        <label>
          Descrição:
          <input
            type="text"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
            className="input-style"
          />
        </label>
      </div>
      <div className="campo">
        <label>
          Data de Vencimento:
          <input
            type="date"
            value={dataVencimento}
            onChange={(e) => setDataVencimento(e.target.value)}
            className="input-style"
          />
        </label>
      </div>
      <div className="campo">
        <label>
          Valor:
          <input
            type="text"
            value={valor}
            onChange={handleValorChange}
            className="input-style"
          />
        </label>
      </div>
      <div className="div-botao">
        <button type="submit" className="botao-inserir">
          <FontAwesomeIcon icon={faPlus} style={{ marginRight: "5px" }} />
          Inserir
        </button>
      </div>
    </form>
  );
};

export default Formulario;
