import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Tabela.css";
import Formulario from "./Formulario";
import { format } from "date-fns";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClose, faPlus, faSave } from "@fortawesome/free-solid-svg-icons";

const Tabela: React.FC = () => {
  const [data, setData] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get("http://localhost:3001/Releases");
      setData(result.data);
    };

    fetchData();
  }, []);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  // Função para formatar a data
  const formatData = (dataString: string) => {
    return format(new Date(dataString), "dd/MM/yyyy");
  };

  // Função para formatar o valor
  const formatValor = (valorString: string) => {
    const valor = parseFloat(valorString.replace(",", "."));
    return valor.toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });
  };

  return (
    <div className="table-container">
      <div className="cadastro-button">
        <button onClick={openModal}>Cadastrar</button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>TIPO</th>
            <th>DESCRIÇÃO</th>
            <th>DATA DE VENCIMENTO</th>
            <th>VALOR</th>
            <th>DATA DE CRIAÇÃO</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item: any) => (
            <tr key={item.ID}>
              <td>{item.ID}</td>
              <td>{item.TIPO}</td>
              <td>{item.DESCRICAO}</td>
              <td>{formatData(item.DATAVENCTO)}</td>
              <td>{formatValor(item.VALOR)}</td>
              <td>{formatData(item.DATACRACAO)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <button onClick={closeModal} className="modal-botao">
            <FontAwesomeIcon icon={faClose} style={{ marginRight: "5px" }} />
          </button>
          <Formulario />
        </div>
      )}
    </div>
  );
};

export default Tabela;
